package com.rackspace.services;/* * Class - ToDoListServiceImpl * */import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.rackspace.schema.service.todolist.v1.ToDoList;import com.rackspace.schema.service.todolist.v1.ToDoLists;import com.rackspace.services.entity.todolist.ToDoListServiceRepository;/** * Service Implementation for getting ToDoList data *  *  */@Service("toDoListService")public class ToDoListServiceImpl implements ToDoListService {	@Autowired(required = true)	ToDoListServiceRepository repository;	public ToDoLists getToDoList(String userId) {		return repository.getToDoList(userId);	}	public ToDoLists getToDoList() {		return repository.getToDoList();	}	public void addToDoList(ToDoList todolist) {		repository.addToDoList(todolist);	}}